import React , { Component} from 'react';

import "./DetectPotential.css";
import Slider from "react-slick";
// import OwlCarousel from 'react-owl-carousel';
// import 'owl.carousel/dist/assets/owl.carousel.css';
// import 'owl.carousel/dist/assets/owl.theme.default.css';
export default class DetectPotential extends Component {
	constructor(props) {
    super(props);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }
  next() {
    this.slider.slickNext();
  }
  previous() {
    this.slider.slickPrev();
  }



	render() {
		  var settings = {
      dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2000,
      pauseOnHover: true
    };
		return(


			<section>
				<div className="detectWrapper">
					<div className="heading4">Detect potential risks in real time</div>
					<div className="underline4"></div>
					<div className="carouselWrapper col-lg-10 col-md-10 col-sm-10 col-xs-10 col-lg-offset-1">
						<div className="row">
							{/*<OwlCarousel className="stage Padding"loop margin={10} nav >


							    <div class="item subImg">
							    	<img src="images/AdobeStock_1.jpeg" />
							    </div>
							    <div class="item subImg">
							    	<img src="images/AdobeStock_1.jpeg"/>
							    </div>
							    <div class="item subImg">
							    	<img src="images/AdobeStock_1.jpeg" />
							    </div>
							    <div class="item subImg">
							    	<img src="images/AdobeStock_1.jpeg"  />
							    </div>   

							</OwlCarousel>	*/}

							<div>
        <h2>Previous and Next methods</h2>
        <Slider ref={c => (this.slider = c)} {...settings}>
          <div key={1}>
            <h3>1</h3>
          </div>
          <div key={2}>
            <h3>2</h3>
          </div>
          <div key={3}>
            <h3>3</h3>
          </div>
          <div key={4}>
            <h3>4</h3>
          </div>
          <div key={5}>
            <h3>5</h3>
          </div>
          <div key={6}>
            <h3>6</h3>
          </div>
        </Slider>
        <div style={{ textAlign: "center" }}>
          <button className="button" onClick={this.previous}>
            Previous
          </button>
          <button className="button" onClick={this.next}>
            Next
          </button>
        </div>
      </div>



						</div>

					</div>
				</div>

			</section>


			)
	}
}
