import React , { Component} from 'react';

import "./Help.css";
import $ from "jquery";
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

export default class Help extends Component {

	render() {
		$(document).ready(function() {

		  $("#owl-demo").owlCarousel({

		      navigation : true, // Show next and prev buttons
		      slideSpeed : 300,
		      paginationSpeed : 400,
		      singleItem:true

		      // "singleItem:true" is a shortcut for:
		      // items : 1, 
		      // itemsDesktop : false,
		      // itemsDesktopSmall : false,
		      // itemsTablet: false,
		      // itemsMobile : false

  });

});    

		return (

			<section>
				<div className="helpWrapper col-lg-12">
					{/*<div className="heading5">How Can We Help?</div>*/}
					<div id="owl-demo" class="owl-carousel owl-theme">

					  <div className="item"><img src="images/AdobeStock_1.jpeg" alt="The Last of us" /></div>
					  <div className="item"><img src="images/AdobeStock_1.jpeg" alt="GTA V" /></div>
					  <div className="item"><img src="images/AdobeStock_1.jpeg" alt="Mirror Edge" /></div>

					</div>

				</div>
			</section>

			)
	}
}
